import Webcam from 'react-webcam';
import React from 'react';
import './App.css';
import { useRef, useState } from 'react';
function Ff() {
    const [img, setImg] = useState([]);
    const winref = useRef(null);
    const [camera, setCamera] = useState(false);
    const [im, setIm] = useState(false);
    const [sto,setSto]=useState(false);
    const[show,setShow]=useState(false);
    const [re,setRe]=useState(false);
    const [rear,setRear]=useState(false);
    function capt() {
        let im = winref.current.getScreenshot();
        setImg((prev)=>[...prev,im]);
        setIm(true);
        setShow(true);
        setCamera(false);
    }
    function cc() {
        setCamera(true);
    }
    // function ca() {
        
    //     setShow(true);
    // }
    function stop(){
        setSto(true)
    }
    function sh(){
        setRe(true)
    }
    function ff(){
        setRear(true);
    }
    function dd(){
        setCamera(true);
    }
    return (
        <>
        <div className="main">
            {(re||camera) && <div className="d2">
                <Webcam className="w" audio={false} ref={winref} videoConstraints={{facingMode:rear?"user":"environment"}} width={200}/> 
            </div>}
            <br/>
            {(!camera && !show) && <div>
                <button className="b1" onClick={cc}>CAPTURE</button>
                {/* <button className="b1" onClick={ca}>SHOW</button><br /> */}
            </div>}
            {(camera||re) && !sto && <div className="d2">
                <button className="b1" onClick={capt}>CLICK</button><br />
                {<button className="b1" onClick={stop}>STOP</button>}
            </div>}
            <div>
                {!rear && camera &&<button onClick={ff}>REAR</button>}
                {rear && camera && <button onClick={ff}>FRONT</button>}
            </div>
            {/* {sto && <button className="b1" onClick={ca}>SHOW</button>} */}
            {show && <div>
               <ul>{im && (
                    img.map((a, index) => (
                        <li key={index}>
                           <h1><img src={a}/></h1>
                        </li>
                    )))
                }
                </ul> 
                <button>OK</button>
                <button onClick={dd}>RETAKE</button>
            </div>}
            {/* <div>
            {re &&
                (<Webcam audio={false} ref={winref}/> )}
            </div> */}
            </div>
        </>
    )
}
export default Ff;
