import Webcam from 'react-webcam';
import React from 'react';
import './App.css';
import { useRef, useState } from 'react';
function Ff() {
    const [img, setImg] = useState([]);
    const winref = useRef(null);
    const [camera, setCamera] = useState(false);
    const [im, setIm] = useState(false);
    function capt() {
        let im = winref.current.getScreenshot();
        setImg((prev)=>[...prev,im]);
    }
    function cc() {
        setCamera(true);
    }
    function ca() {
        setIm(true);
    }
    return (
        <>
            <div className="d2">
                {camera &&
                (<Webcam audio={false} ref={winref}/> )}
            </div>
            {!camera && <div className="d2">
                <button onClick={cc}>CAPTURE</button>
                <button onClick={ca}>SHOW</button><br />
            </div>}
            {camera && <div className="d2">
                <button onClick={capt}>CLICK</button><br />
                <button onClick={stop}>STOP</button>
            </div>}
            {/* <div>
                <ul>{im && (
                    img.map((a, index) => (
                        <li key={index}>
                            <img src={a} alt="Captured" width={200} />
                        </li>
                    )))
                }
                </ul> */}
            {/* </div> */}

        </>
    )
}
export default Ff;
