import Webcam from 'react-webcam';
import React from 'react';
import './App.css';
import { useRef, useState } from 'react';
function Ff() {
    let arr=new Array();
    const [img, setImg] = useState([]);
    const winref = useRef(null);
    const [camera, setCamera] = useState(false);
    const [im, setIm] = useState(false);
    const [sto,setSto]=useState(false);
    const[show,setShow]=useState(false);
    const [re,setRe]=useState(false);
    const [rear,setRear]=useState(false);
    const[ok,setOk]=useState(false);
    function capt() {
        let im = winref.current.getScreenshot();
        setImg((prev)=>[...prev,im]);
        setIm(true);
        setShow(true);
        setCamera(false);
    }
    function cc() {
        setCamera(true);
    }
    // function ca() {
        
    //     setShow(true);
    // }
    function stop(){
        setSto(true);
        setCamera(false);
    }
    function sh(){
        setRe(true)
    }
    function ff(){
        setRear(true);
    }
    function dd(){
        setCamera(true);
    }
    function f(){
        setRear(false);
    }
    function ss(){
        setOk(true);
    }
    function clearImages() {
        setImg([]);
        setShow(false);
      }
      
    return (
        <>
        <div className="main">
            <div className="d2">
                {(camera) && <Webcam className="w" audio={false} ref={winref} videoConstraints={{facingMode:rear?"environment":"user"}} width={1920} height={ 1080}/>}
                {/* {!rear && camera &&<button onClick={ff}>REAR</button>}
                {rear && camera && <button onClick={f}>FRONT</button>} */}
               <div>
                {!rear && camera &&<button onClick={ff}>REAR</button>}
                {rear && camera && <button onClick={f}>FRONT</button>}
                </div>
            </div>
            <br/>
            
            {(!camera && (!show || sto)) && <div>
                <button className="b1" onClick={cc}>CAPTURE</button>
                {/* <button className="b1" onClick={ca}>SHOW</button><br /> */}
            </div>}
            {(camera||re) && <div >
                <button className="b1" onClick={capt}>CLICK</button><br />
                {<button className="b1" onClick={stop}>STOP</button>}
            </div>}
            <div>
            {show && <button onClick={clearImages}>CLEAR</button>}
            </div>
            {/* {sto && <button className="b1" onClick={ca}>SHOW</button>} */}
            {show && <div>
              {!re&&(im||ok) &&(
                    img.map((a, index) => (
                           <h1><img src={a}/></h1>
                    )))
                } 
                {show && <button onClick={ss}>OK</button>}
                {show && <button onClick={dd}>RETAKE</button>}
                {arr.forEach((i)=>{
                    console.log(i);
                })}
            </div>}
            {/* <div>
            {re &&
                (<Webcam audio={false} ref={winref}/> )}
            </div> */}
            </div>
        </>
    )
}
export default Ff;
