import Webcam from 'react-webcam';
import React from 'react';
import './App.css';
import  {useRef,useState}  from 'react';
function Ff(){
    const[img,setImg]=useState([]);
    const winref=useRef(null);
    const[ocamera,setCamera]=useState(false);
    const [im,setIm]=useState(false);
    function capt(){
        let im=winref.current.getScreenshot();
        setImg((prev)=>
            [...prev,im]
        )
    }
    function cc(){
        setCamera(true);
    }
    function ca(){
        setIm(true);
    }
    return(
        <>
        <div className="d2">
            <Webcam audio={false} ref={winref}/>
            <button onClick={cc}></button>
            <button onClick={capt}>Capture</button>
            <button onClick={ca}>Show</button>
        </div>
            <div>
            <ul>{im && (
                    img.map((a, index) => (
                            <li key={index}>
                                <img src={a} alt="Captured" width={200} />
                            </li>
                        )))
            }
             </ul>
            </div>
        
        </>
    )
}
export default Ff;
