import React from 'react';
import {useState} from 'react';
import {socket} from 'socket-io';
function In(){
    const [msg,setMsg]=useState("");
    function on(e){
        setMsg((prev)=>{[e.target.name,e.target.value]});
        socket.emit("message",msg);
    }
    return(
        <>
        <div className="o3">
            <form onSubmit={on}> 
            <input type="text" className="ii" name="msg" value={msg}/>
            <input type="submit"></input>
            </form>
        </div>
        </>
    )
}
export default In;
