import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import './App.css';
const socket = io('http://localhost:3001');
function Sh() {
    const [mess, setMess] = useState([]);
    const [input, setInput] = useState("");
    const[r_mess,setR_mess]=useState([]);  
    const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString());
    function on(e){
        setMess((prev)=>[...prev,{text:input,time:new Date().toLocaleTimeString()}]);
        setInput("");
        socket.broadcast.emit(("me",(mess)));
        setR_mess((prev)=>[...prev,r_mess]);
    }
    function oon(e){
        setInput(e.target.value);
    }
    useEffect(() => {
        socket.on("rec_msg", (msg) => {
            setMess((prev) => [...prev, { text: msg, time: new Date().toLocaleTimeString() }]);
        });
    
        return () => socket.off("receive");
    }, []); 
    // useEffect(() => {
    //     socket.on("mm", (msg) => {
    //         setMess((prev) => [...prev, msg]);
    //     });

    //     // Cleanup to prevent multiple bindings
    //     return () => socket.off("mm");
    // }, []);
    // const msgg = [
    //     {
    //         m: "Hello This is Sathwik,Hope You Remembered Me"
    //     },
    //     {
    //         m: "Hello This is Vignesh,Hope You Remembered Me"
    //     },
    //     {
    //         m: "Hello This is Lokesh,Hope You Remembered Me"
    //     }
    // ]
    return (
        <>
            <div className="dd">
                <header className="hh"><h1 className="hd">Let's Chat</h1></header>
                <br/>
                {mess.map((i,index)=>(
                    <div className="hh2"><div className="hh3">{i.text}<footer className="fff"><small><sub>{i.time}</sub></small></footer></div></div>))}
                {r_mess.map((n,index)=>(
                    <div className="hh2"><div className="hh3">{n.text}<footer className="fff"><small><sub>{n.time}</sub></small></footer></div></div>
                ))}
                <div className="iii">
                    <input className="iiiii" type="text" name="input" value={input} onChange={oon}/>
                    <button className="bb" type="submit" onClick={on}>ðŸš€</button>
                </div>
            </div>
        </>
    )
}
export default Sh;
