import React,{useState} from 'react';
import {io} from 'socket.io-client';
const socket = io('http://localhost:3000');
function Sh(){
    const[mess,setMess]=useState([]);
    socket.on("msg",(msg)=>{
        setMess((prev)=>[...prev,msg]);
    })
    return(
        <>
        <div className="dd">{mess.map((m,index)=>(
            <h1>{m}</h1>
        ))}
        </div>
        </>
    )
}
export default Sh;
